import React from 'react'

export const MapDemo6 = () => {

  const products = [
    { id: 1, name: "Laptop", price: 60000, stock: 5, category: "Electronics", img: "data:image/jpeg;base64," },
    { id: 2, name: "Mobile", price: 15000, stock: 0, category: "Electronics", img: "" },
    { id: 3, name: "Chair", price: 2000, stock: 10, category: "Furniture", img: "data:image/jpeg;base64," },
    { id: 4, name: "Table", price: 8000, stock: 2, category: "Furniture", img: "" },
    { id: 5, name: "Headphones", price: 3000, stock: 15, category: "Accessories", img: "data:image/jpeg;base64," },
  ]

  return (
    <div>
      <h2>MapDemo6 - Product Table</h2>

      <table border="1px" cellPadding="10">
        <thead>
          <tr>
            <th>ID</th>
            <th>Image</th>
            <th>Name</th>
            <th>Price</th>
            <th>Stock</th>
            <th>Category</th>
          </tr>
        </thead>

        <tbody>
          {
          products.map((p) => (
            <tr
              key={p.id}
              style={{
                backgroundColor: p.stock === 0 ? "#ffcccc" : "#0c0b09"
              }}
            >
              <td>{p.id}</td>

              <td>
                {p.img ? (
                  <img src={p.img} alt="product" />
                ) : (
                  <span style={{ color: "yellow" }}>Image is missingâ€¦</span>
                )}
              </td>

              <td style={{ fontWeight: p.price > 50000 ? "bold" : "normal" }}>
                {p.name}
              </td>

              <td style={{ color: p.price > 10000 ? "red" : "green" }}>
                {p.price}
              </td>

              <td style={{ color: p.stock < 5 ? "green" : "red" }}>
                {p.stock}
              </td>

              <td>{p.category}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  )
}
