import React from "react";
import { useParams } from "react-router-dom";

export const Watch = () => {
    const { name } = useParams();

    // Movie images mapping
    const movieImages = {
        Dhurandhar: "data:image/jpeg;base64,",
        "Border 2": "data:image/jpeg;base64,",
        Laalo: "data:image/jpeg;base64,",
    };

    return (
        <div style={{ textAlign: "center", marginTop: "30px" }}>
            <h1>Watching Movie.. {name}</h1>

            <img
                src={movieImages[name]}
                alt={name}
                style={{
                    width: "300px",
                    height: "450px",
                    marginTop: "20px",
                    borderRadius: "10px",
                    boxShadow: "0 4px 10px rgba(0,0,0,0.3)",
                }}
            />
        </div>
    );
};
